/*SELECT TRIM(AC021_EMP_ID) AS CODE_ID *//* 사번 *//* , TRIM(AC021_EMP_NM)||'('||TRIM(AC021_EMP_ID)||')' AS CODE_NM *//* 성명 한글 *//* , TRIM(AC021_EMP_NM) AS CODE_NM1 *//* 성명 한자 *//* , TRIM(AC021_EMP_NM) AS CODE_NM2 *//* 성명 영문 *//* , (CASE WHEN TRIM(AC021_HNO_NO1) || '-' || TRIM(CIPDEC('df',AC021_HNO_NO2)) || '-' || TRIM(AC021_HNO_NO3) ='--' THEN '' ELSE TRIM(AC021_HNO_NO1) || '-' || TRIM(CIPDEC('df',AC021_HNO_NO2)) || '-' || TRIM(AC021_HNO_NO3) END) AS CPHON_IDV_TNO *//* 핸드폰 번호 *//* , (CASE WHEN AC021_EMP_ID = '32000' THEN '70569' ELSE TRIM(AC021_DEPT_CD) END) AS OG_CD *//* 부서코드 *//* , (CASE WHEN AC021_EMP_ID = '32000' THEN '인천공장서비스센터' ELSE TRIM(AC021_DEPT_NM) END) AS OG_NM *//* 부서명 *//* , TRIM(AC021_CGUB) AS EMP_DV_VAL *//* 사원구분 1 정규직, 2 직무사원 또는 계약직 *//* , TRIM('') AS EMP_RSB_DV_VAL *//* 직책 *//* , TRIM('') AS EMP_TP_VAL *//* 사원유형 *//* , TRIM(AC021_INT_DT) AS ENTCO_DT *//* 입사일자 *//* , TRIM(AC021_RET_DT) AS RSGN_DT *//* 퇴사일자 *//* , TRIM(AC021_JMCD) AS EMP_ROL_DV_VAL *//* 직무 *//* , (CASE WHEN TRIM(AC021_CGUB) = '1' THEN '정규직' WHEN TRIM(AC021_CGUB) = '2' AND TRIM(AC021_GRAD) = '86' THEN '직무사원(1급)' WHEN TRIM(AC021_CGUB) = '2' AND TRIM(AC021_GRAD) = '87' THEN '직무사원(2급)' WHEN TRIM(AC021_CGUB) = '2' AND TRIM(AC021_GRAD) = '88' THEN '직무사원(3급)' WHEN TRIM(AC021_CGUB) = '2' AND TRIM(AC021_GRAD) = '95' THEN '계약직' ELSE '' END) AS EMP_DV_VAL_NM *//* 사원구분 명 *//* , (CASE WHEN TRIM(AC021_JMCD) = '55' THEN '소장' WHEN TRIM(AC021_CGUB) = '56' THEN '자재실장' ELSE '엔지니어' END) AS EMP_ROL_DV_VAL_NM *//* 직무 명 *//* FROM KWAS.LC_ALLOCATE_AC021TB WHERE (AC021_DEPT_CD LIKE 'V%' OR (AC021_DEPT_CD2 IN (SELECT ISDEPT FROM KWMART.IS2500P WHERE ISUDPT = '71314') OR AC021_DEPT_CD IN (SELECT ISDEPT FROM KWMART.IS2500P WHERE ISUDPT = '71314') OR AC021_DEPT_CD2 = '71314' OR AC021_EMP_ID = '32000')) *//*상위 소속이 Wells서비스관리팀 또는 Wells고객서비스부문*//* AND AC021_RET_DT IS NULL ORDER BY RSGN_DT, OG_CD, EMP_DV_VAL, EMP_ROL_DV_VAL_NM, EMP_ROL_DV_VAL DESC*/
