SELECT T1.PRTNR_NO AS CODE_ID , T1.PRTNR_KNM || '(' || T1.PRTNR_NO || ')' AS CODE_NM , T1.PRTNR_KNM AS CODE_NM1 , T1.PRTNR_ENM AS CODE_NM2 , '' AS CPHON_IDV_TNO , (CASE WHEN T1.PRTNR_NO = '32000' THEN '70569' ELSE T1.OG_CD END) AS OG_CD , (CASE WHEN T1.PRTNR_NO = '32000' THEN '인천공장서비스센터' ELSE T1.OG_NM END) AS OG_NM , '' AS EMP_DV_VAL , '' AS EMP_RSB_DV_VAL , '' AS EMP_TP_VAL , T1.CNTR_DT AS ENTCO_DT , T1.CLTN_DT AS RSGN_DT , T1.ROL_DV_CD AS EMP_ROL_DV_VAL , '' AS EMP_DV_VAL_NM , '' AS EMP_ROL_DV_VAL_NM , T1.OG_TP_CD AS OG_TP_CD FROM TB_OGBS_MM_PRTNR_IZ T1 WHERE T1.BASE_YM = '202212' AND (T1.OG_CD IN (SELECT A.OG_CD FROM TB_OGBS_MM_OG_IZ A WHERE A.CLO_DT IS NULL) OR T1.PRTNR_NO = '32000') ORDER BY EMP_ROL_DV_VAL DESC, CODE_NM, RSGN_DT, OG_CD, EMP_DV_VAL, EMP_ROL_DV_VAL_NM
