mapper.select:
SELECT SM.CNTR_NO , SM.CNTR_SN , CD.SELL_TP_CD , CD.SELL_TP_DTL_CD , CD.CNTR_PTRM , CASE WHEN CD.CNTR_PTRM < SM.RENTAL_TN+1 THEN CD.CNTR_PTRM ELSE SM.RENTAL_TN+1 END RENTAL_TN , NULL AS CNTR_PASG_DC , NULL AS SPP_NMN_N , CD.CNTR_PD_STRTDT , SM.NOM_SL_AMT , SM.SPMT_SL_AMT , SM.NOM_DSC_AMT , SM.SPMT_DSC_AMT , NVL(#{slCtrAmt}, 0) AS SL_CTR_AMT -- , NULL AS SL_DC -- , NULL AS RTNGD_QTY /* 화면에서 입력하는 값일 경우, null을 덮어쓰지 않기 위해 주석 처리 */ -- , 0 AS CAN_CTR_AMT /* 화면에서 입력하는 값일 경우, null을 덮어쓰지 않기 위해 주석 처리 */ , T5.SL_SUM_AMT AS SL_SUM_AMT , T5.SL_SUM_AMT - TRUNC(T5.SL_SUM_AMT/1.1,0) AS SL_SUM_VAT , NVL(SM.SL_AGG_AMT,0) + T5.SL_SUM_AMT AS SL_AGG_AMT , (NVL(SM.SL_AGG_AMT,0) + T5.SL_SUM_AMT) - TRUNC((NVL(SM.SL_AGG_AMT,0) + T5.SL_SUM_AMT)/1.1,0) AS SL_AGG_AMT_VAT , SM.DSC_AGG_AMT /* 할인누계 */ , SM.CTR_AGG_AMT /* 조정누계 */ , SM.NOM_INT_AMT /* 정상이자금액 */ , SM.INT_CTR_AMT /* 이자조정금액 */ , SM.INT_SUM_AMT /* 이자합계금액 */ , SM.INT_VAT /* 이자부가가치세 */ , SM.INT_AGG_AMT /* 이자누계금액 */ , SM.INT_DSC_AGG_AMT /* 이자할인누계금액 */ , 0 AS THM_PAIAM /* 당월원리금 - 금융리스만 다룸, 다음 STEP에서 다시 계산해야 함 */ , 0 AS THM_SV_AMT /* 당월서비스금액 - 금융리스만 다룸, 다음 STEP에서 다시 계산해야 함 */ , 0 AS SL_BLAM /* 매출잔액 = TO-BE 매출 테이블에서 다루지 않음, 우선 0 */ , 0 AS ADN_SV_SPMT_SL_AMT /* 부가서비스추가매출금액 */ , SM.PRM_BLAM_EOT_AMT AS PRM_BTD_AMT /* 선납기초금액 */ , SM.EOT_ATAM AS EOT_ATAM /* 기말선수금 */ , SM.PRM_BLAM_EOT_AMT + SM.EOT_ATAM AS TOT_PRPD_AMT /* 총선수금액 */ , NVL(T7.SL_DP_AMT, 0) AS SL_DP_AMT /* 매출입금금액 */ , NVL(T7.SL_DP_AGG_AMT, 0) AS SL_DP_AGG_AMT /* 매출입금누계금액 */ , NVL(T7.UC_AMT, 0) AS UC_AMT /* 미수금액 */ , NVL(DB.EOT_DLQ_AMT,0) AS DLQ_AMT , T6.PRM_RFND_AMT AS PRM_RFND_AMT , T6.PRPD_RFND_AMT AS PRPD_RFND_AMT , NVL(#{dscDdctam}, 0) AS DSC_DDCTAM , NVL(#{filtDdctam}, 0) AS FILT_DDCTAM , 0 AS RENTAL_RGST_COST_RFND_AMT , 0 AS RENTAL_RGST_COST_RFND_AMT_VAT , NVL(BA.BOR_AMT, '0') AS BOR_AMT , ((NVL(SM.PRM_BLAM_EOT_AMT,0) + NVL(SM.EOT_ATAM,0)) - NVL(T7.SL_DP_AMT,0)- NVL(T7.UC_AMT,0) - NVL(#{dscDdctam}, 0) - NVL(#{filtDdctam}, 0) - NVL(DB.THM_DLQ_ADD_DP_SUM_AMT,0)) AS TOT_RFND_AMT , NVL(BA.RES_RTLFE_BOR_AMT, 0) AS RES_RTLFE_BOR_AMT , NVL(BA.RGST_COST_DSC_BOR_AMT, 0) AS RGST_COST_DSC_BOR_AMT /* 등록비할인위약금액 */ , NVL(BA.RENTAL_DSC_BOR_AMT, 0) AS RENTAL_DSC_BOR_AMT /* 렌탈할인위약금액 */ , NVL(BA.CSMB_COST_BOR_AMT, 0) AS CSMB_COST_BOR_AMT /* 소모품비위약금액 */ , NVL(BA.P_BOR_AMT, 0) AS PT_BOR_AMT /* 포인트위약금액 */ , NVL(BA.REQD_CS_BOR_AMT, 0) AS REQD_CS_BOR_AMT /* 철거비용위약금액 */ , NVL(DB.EOT_DLQ_ADD_AMT, 0) AS EOT_DLQ_ADD_AMT /* 기말연체가산금액 */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ SM INNER JOIN TB_SSCT_CNTR_DTL CD ON CD.CNTR_NO = SM.CNTR_NO AND CD.CNTR_SN = SM.CNTR_SN AND CD.SELL_TP_CD = #{sellTpCd} AND CD.DTA_DL_YN = 'N' -- AND CD.ALNCMP_CD NOT IN ('45','69') LEFT OUTER JOIN TB_CBCL_DLQ_BAS DB ON DB.CNTR_NO = SM.CNTR_NO AND DB.CNTR_SN = SM.CNTR_SN AND DB.PERF_YM = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') LEFT OUTER JOIN TB_CBCL_WELLS_BOR_AMT_BAS BA ON BA.CNTR_NO = CD.CNTR_NO AND BA.CNTR_SN = CD.CNTR_SN AND BA.PERF_YM = SUBSTR(#{cancelDt},1,6) INNER JOIN LATERAL ( SELECT (NVL(SM.NOM_SL_AMT,0) + NVL(SM.SPMT_SL_AMT,0) + NVL(SM.SPMT_DSC_AMT,0) - NVL(#{slCtrAmt}, 0)) AS SL_SUM_AMT FROM DUAL ) T5 ON 1=1 INNER JOIN LATERAL ( SELECT CASE WHEN NVL(T5.SL_SUM_AMT,0) > 0 THEN CASE WHEN NVL(SM.PRM_BLAM_EOT_AMT,0) > NVL(T5.SL_SUM_AMT,0) THEN NVL(T5.SL_SUM_AMT,0) WHEN NVL(SM.EOT_ATAM,0) > 0 THEN CASE WHEN NVL(SM.PRM_BLAM_EOT_AMT,0) + NVL(SM.EOT_ATAM,0) - NVL(T5.SL_SUM_AMT,0) - NVL(DB.THM_DLQ_ADD_DP_SUM_AMT,0) > 0 THEN NVL(T5.SL_SUM_AMT,0) ELSE NVL(T5.SL_SUM_AMT,0) + (NVL(SM.PRM_BLAM_EOT_AMT,0) + NVL(SM.EOT_ATAM,0) - NVL(T5.SL_SUM_AMT,0) - NVL(DB.THM_DLQ_ADD_DP_SUM_AMT,0)) END ELSE SM.PRM_BLAM_EOT_AMT END ELSE 0 END AS SL_DP_AMT_1 , CASE WHEN NVL(T5.SL_SUM_AMT,0) > 0 THEN CASE WHEN NVL(SM.PRM_BLAM_EOT_AMT,0) > NVL(T5.SL_SUM_AMT,0) THEN NVL(SM.PRM_BLAM_EOT_AMT,0) - NVL(T5.SL_SUM_AMT,0) WHEN NVL(SM.EOT_ATAM,0) > 0 THEN 0 ELSE 0 END ELSE NVL(SM.PRM_BLAM_EOT_AMT,0) END AS PRM_RFND_AMT , CASE WHEN T5.SL_SUM_AMT > 0 THEN CASE WHEN SM.PRM_BLAM_EOT_AMT > T5.SL_SUM_AMT THEN SM.EOT_ATAM WHEN SM.EOT_ATAM > 0 THEN CASE WHEN SM.PRM_BLAM_EOT_AMT + SM.EOT_ATAM - T5.SL_SUM_AMT - DB.THM_DLQ_ADD_DP_SUM_AMT > 0 THEN SM.PRM_BLAM_EOT_AMT + SM.EOT_ATAM - T5.SL_SUM_AMT - DB.THM_DLQ_ADD_DP_SUM_AMT ELSE 0 END ELSE 0 END ELSE SM.PRM_BLAM_EOT_AMT END AS PRPD_RFND_AMT FROM DUAL ) T6 ON 1=1 INNER JOIN LATERAL (
SELECT CASE WHEN NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) < 0 THEN T6.SL_DP_AMT_1 + NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) ELSE T6.SL_DP_AMT_1 END AS SL_DP_AMT , CASE WHEN NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) < 0 THEN NVL(SM.SL_DP_AGG_AMT,0) + T6.SL_DP_AMT_1 + NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) ELSE NVL(SM.SL_DP_AGG_AMT,0) + T6.SL_DP_AMT_1 END AS SL_DP_AGG_AMT , CASE WHEN NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) < 0 THEN 0 ELSE NVL(SM.SL_AGG_AMT,0) - NVL(SM.SL_DP_AGG_AMT,0) - T6.SL_DP_AMT_1 + NVL(DB.EOT_DLQ_ADD_AMT,0) END AS UC_AMT FROM DUAL
) T7 ON 1=1 WHERE SM.DTA_DL_YN = 'N' AND SM.CNTR_NO = #{cntrNo} AND SM.CNTR_SN = #{cntrSn} AND SM.SL_CL_YM = TO_CHAR(ADD_MONTHS(TO_DATE(SUBSTR(#{cancelDt},1,6), 'YYYYMM'), -1),'YYYYMM')

mapper.insert:
