mapper.update:
UPDATE TB_SSCT_CNTR_BAS
   SET CNTR_PRGS_STAT_CD = #{cntrPrgsStatCd}
     , CNTR_TEMP_SAVE_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')

 WHERE CNTR_NO = #{cntrNo}

mapper.select:
SELECT CNTR_CST_REL_ID /* 계약고객관계ID */ , VL_STRT_DTM /* 유효시작일시 */ , VL_END_DTM /* 유효종료일시 */ , CNTR_UNIT_TP_CD /* 계약단위유형코드 */ , CNTR_NO /* 계약번호 */ , DTL_CNTR_NO /* 상세계약번호 */ , DTL_CNTR_SN /* 상세계약일련번호 */ , COPN_DV_CD /* 법인격구분코드 */ , CST_NO /* 고객번호 */ , CNTR_CST_REL_TP_CD /* 계약고객관계유형코드 */ , CNTRT_REL_CD /* 계약자관계코드 */ , INSI_CST_TP_CD /* 내부고객유형코드 */ , LRNN_GRY_CD /* 학습학년코드 */ , CNTR_CH_RCP_ID /* 계약변경접수ID */ , DTA_DL_YN /* 데이터삭제여부 */ , FST_RGST_DTM /* 최초등록일시 */ , FST_RGST_USR_ID /* 최초등록사용자ID */ , FST_RGST_PRG_ID /* 최초등록프로그램ID */ , FST_RGST_DEPT_ID /* 최초등록부서ID */ , FNL_MDFC_DTM /* 최종수정일시 */ , FNL_MDFC_USR_ID /* 최종수정사용자ID */ , FNL_MDFC_PRG_ID /* 최종수정프로그램ID */ , FNL_MDFC_DEPT_ID /* 최종수정부서ID */ FROM TB_SSCT_CNTR_CST_REL /* 계약고객관계 */ WHERE 1 = 1 AND DTL_CNTR_NO = #{cntrNo}
AND DTL_CNTR_SN = #{cntrSn} /* 계약일련번호 */
AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN VL_STRT_DTM AND VL_END_DTM AND DTA_DL_YN = 'N'
