WITH CNTR_MAST AS ( SELECT T11.CNTR_NO /* 계약번호 : GRP_WPN_SEQ */ , MIN(T11.CNTR_SN) AS CNTR_SN /* 계약일련번호 (WPN_SEQ) , ORDR_NO(TOBE는 ORDR_NO도 통합) */ , COUNT(1) AS CNTR_CT /* 계약건수 (CONC_CNT) */ , SUM(CASE WHEN T11.SELL_TP_CD = '2' THEN T11.PD_QTY ELSE 0 END) AS PD_QTY /* ORDR_CNT */ , MIN(T10.CNTR_PRGS_STAT_CD) AS CNTR_PRGS_STAT_CD /* 계약진행상태코드(STAT_CD) */ FROM TB_SSCT_CNTR_BAS T10 /* T.계약기본 */ INNER JOIN TB_SSCT_CNTR_DTL T11 /* T.계약상세 */ ON T11.CNTR_NO = T10.CNTR_NO /* 계약번호 */ AND T11.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_PRTNR_IZ T30 /* T.월파트너내역 - 판매자 */ ON T30.BASE_YM = SUBSTR(T10.CNTR_RCP_FSH_DTM,1,6) AND T30.OG_TP_CD = T10.SELL_OG_TP_CD AND T30.PRTNR_NO = T10.SELL_PRTNR_NO AND T30.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ T31 /* T.월조직내역 - 조직정보 */ ON T31.BASE_YM = SUBSTR(T10.CNTR_RCP_FSH_DTM,1,6) AND T31.OG_ID = T30.OG_ID AND T31.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CUBS_CST_BAS T40 /* T.고객기본 - 계약자 */ ON T40.CST_NO = T10.CNTR_CST_NO /* 고객번호*/ AND T40.DTA_DL_YN = 'N' /* 데이터삭제여부 */ WHERE 1=1 AND T10.DTA_DL_YN = 'N' AND T11.CNTRW_TP_CD NOT IN ('9')
/* 신규 */ AND T10.CNTR_CH_RCP_ID IS NULL --MAST.RGSN_DIV(등록구분)
/* 변경 */ AND T10.CNTR_CH_RCP_ID IS NOT NULL --MAST.RGSN_DIV(등록구분)
/* 신규/변경 */ AND 1 = 1
AND T10.CNTR_RCP_FSH_DTM BETWEEN #{cntrRcpStrtdt}||'000000' AND #{cntrRcpEnddt}||'235959' /* MAST.ACPG_DT */
/* 계약서구분(계약서유형코드) */
AND T11.CNTRW_TP_CD = #{cntrwTpCd}
/* 계약서구분2(상조관련) */ AND T11.ALNCMP_CD IN (
'22'
#{codeId}
)
