mapper.select:
WITH T11 AS ( /* 화면 필수 기준으로 대상 조회 */ SELECT /*+ MATERIALIZE */ --T11.* T11.CNTR_NO , T11.CNTR_SN , T11.BASE_PD_CD , T11.PD_QTY , T11.CNTR_DTL_STAT_CD , T11.CNTRW_TP_CD , T11.FRISU_YN , T11.RSTL_YN , T11.SV_PRD , T11.SELL_DSC_DV_CD , T11.CNTR_AMT , T11.FNL_AMT , T11.FST_RGST_DTM , T11.PD_GD_CD , T11.FST_RGST_USR_ID , T11.FST_RGST_PRG_ID , T11.CTT_RS_CD , T11.ALNCMP_CD , T11.ALNCMP_CNTR_DRM_VAL , T11.SELL_DSC_TP_CD , T11.FEE_FXAM_YN , T11.FEE_ACKMT_BASE_AMT , T11.ACKMT_PERF_RT , T11.ACKMT_PERF_AMT , T11.BOO_SELL_TP_CD , T11.DSC_AMT , T11.CNTRAM_DSC_AMT , T11.CNTR_PD_ENDDT , T11.STPL_PTRM , T11.STPL_PTRM_UNIT_CD , T11.CNTR_PTRM , T11.CNTR_PD_STRTDT , T11.SPP_DUEDT , T11.SELL_TP_CD , T10.CNTR_CST_NO , T12.IST_DT , T12.REQD_DT --, T12.RECAP_DUTY_PTRM_N , T11.STPL_PTRM AS RECAP_DUTY_PTRM_N , T12.SELL_EV_CD , T12.FRISU_BFSVC_PTRM_N , T12.IST_AK_ARTC_MO_CN , T12.SCON_CN , T12.USE_ELECT_TP_CD , T12.PRM_APY_DV_CD , T12.WPRS_ITST_TP_CD , T12.WTQLTY_TST_YN , T12.WRFR_IST_MTH_CD , T12.FRISU_RCVRY_TP_CD , T12.SRCWT_TP_CD --, T13.HIST_STRT_DTM , (CASE WHEN T11.CNTR_DTL_STAT_CD IN ('301','302','303') /* '301' : 고객요청해약,'302' : 연체해약, '303' : 계약취소*/ THEN T11.CNTR_PD_ENDDT ELSE '' END) AS CAN_DT , T43.VST_RQDT /* 방문요청일자 - 의뢰일자(LCTRUT,LCTRUY) */ , F_CMZ_CD_NM('TNT_WELLS', 'SPP_MTHD_TP_CD' ,T43.SPP_MTHD_TP_CD) AS SPP_MTHD_TP_NM /* 배송방식유형코드 - 의뢰구분(LCTGUB, LCTGUB_NM) */ , T44.RCGVP_KNM /* 수령자한글명 - 설치처정보 - 설치자 명(LCWNAM)*/ , T44.CRAL_LOCARA_TNO AS IST_CRAL_LOCARA_TNO /* 설치자 - 휴대전화번호1 */ , T44.MEXNO_ENCR AS IST_MEXNO_ENCR /* 설치자 - 휴대전화번호2 */ , T44.CRAL_IDV_TNO AS IST_CRAL_IDV_TNO /* 설치자 - 휴대전화번호3 */ , T44.CRAL_LOCARA_TNO || '-' || T44.MEXNO_ENCR || '-' || T44.CRAL_IDV_TNO AS IST_CRAL_TNO /* 설치자 - 휴대전화번호(파라미터) */ , T45.NEW_ADR_ZIP AS IST_ADR_ZIP /* 설치처정보 - 우편번호 : LCWZIP */ , T45.RNADR AS IST_RNADR /* 설치처정보 - 기준주소 : LCWAD1 + LCWAD2 +LCWAD3 */ , T45.RDADR AS IST_RDADR /* 설치처정보 - 상세주소 : LCWAD1 + LCWAD2 +LCWAD3 */ , T50.SL_RCOG_DT , T50.RENTAL_TN , T50.RENTAL_PTRM , T50.RENTAL_PTRM2 FROM TB_SSCT_CNTR_BAS T10 /* T.계약기본 */ INNER JOIN TB_SSCT_CNTR_DTL T11 /* T.계약상세 */ ON T11.CNTR_NO = T10.CNTR_NO /* 계약번호 */ AND T11.SELL_TP_CD IN ('2') /* 판매유형코드 : 할부,렌탈,멤버십,정기배송*/ AND T11.DTA_DL_YN = 'N' AND T10.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL T12 /* T.WELLS상세 */ ON T12.CNTR_NO = T11.CNTR_NO /*계약번호 */ AND T12.CNTR_SN = T11.CNTR_SN /*계약번호 */ AND T12.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_ADR_REL T43 /* T.계약주소관계 - 설치 */ ON T43.DTL_CNTR_NO = T11.CNTR_NO AND T43.DTL_CNTR_SN = T11.CNTR_SN AND T43.ADRPC_TP_CD = '3' /* 주소지유형코드 : 설치처(3) */ AND T43.VL_END_DTM = '99991231235959' /* 유효종료일시 */ AND T43.DTA_DL_YN = 'N' --AND EXISTS (SELECT T11.CNTR_NO, T11.CNTR_SN FROM T11 WHERE T11.CNTR_NO = T43.DTL_CNTR_NO AND T11.CNTR_SN = T43.DTL_CNTR_SN) LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS T44 /* T.계약주소지기본 - 설치 */ ON T44.CNTR_ADRPC_ID = T43.CNTR_ADRPC_ID AND T44.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS T45 /* T.주소기본 - 설치 */ ON T45.ADR_ID = T44.ADR_ID AND T45.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ T50 /* WELLS매출월마감내역 */ ON T50.SL_CL_YM = TO_CHAR(SYSDATE, 'YYYYMM') /* 매출마감년월 */ AND T50.CNTR_NO = T11.CNTR_NO AND T50.CNTR_SN = T11.CNTR_SN AND T50.DTA_DL_YN = 'N' WHERE 1=1 AND T11.SELL_TP_CD IN ('2') /* 판매유형코드 : 할부,렌탈,멤버십,정기배송*/
/* 접수일 */ AND T10.CNTR_RCP_FSH_DTM BETWEEN #{strtDt}||'000000' AND #{endDt}||'235959'
/* 매출일 */ AND T50.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt}
/* 취소일 */ --AND T13.HIST_STRT_DTM BETWEEN #{strtDt}||'000000' AND #{endDt}||'235959' AND (CASE WHEN T11.CNTR_DTL_STAT_CD IN ('301','302','303') /* '301' : 고객요청해약,'302' : 연체해약, '303' : 계약취소*/ THEN T11.CNTR_PD_ENDDT ELSE '' END) BETWEEN #{strtDt}||'000000' AND #{endDt}||'235959'
/* 설치일 */ AND T12.IST_DT BETWEEN #{strtDt} AND #{endDt}
/* 만료일 */ AND T12.IST_DT IS NOT NULL AND (CASE WHEN 1 >= T11.CNTR_PTRM OR T11.CNTR_PTRM >= 999 THEN '' ELSE TO_CHAR(ADD_MONTHS(TO_DATE(T12.IST_DT,'YYYYMMDD'), T11.CNTR_PTRM) -1, 'YYYYMMDD') /* 매출일(설치일) + 계약기간 -1 */ END ) BETWEEN #{strtDt} AND #{endDt} -- 상품시작일자 / 설치일자와 값이 달라서 설치일자 맵핑으로 표시되어 설치일자로 수정 -- AND (CASE WHEN 1 >= T11.CNTR_PTRM OR T11.CNTR_PTRM >= 999 THEN '' -- ELSE TO_CHAR(ADD_MONTHS(TO_DATE(T11.CNTR_PD_STRTDT,'YYYYMMDD'), T11.CNTR_PTRM) -1, 'YYYYMMDD') /* 매출일(상품시작일) + 계약기간 -1 */ -- END ) BETWEEN #{strtDt} AND #{endDt}
/* 렌탈차월 */ AND T50.RENTAL_TN = #{rentalNmn}
/* 예정일 */ AND T11.SPP_DUEDT BETWEEN #{strtDt} AND #{endDt}
AND T11.BASE_PD_CD LIKE '%' || #{pdCd} || '%'
AND T11.ALNCMP_CD = #{alncmpCd}
AND T12.SELL_EV_CD = #{sellEvCd}
AND T10.SELL_PRTNR_NO = #{sellPrtnrNo}
AND T10.SELL_OG_TP_CD IN (
