) , T85 AS ( SELECT * FROM (SELECT /*+ MATERIALIZE USE_NL(T11 T850 T851) */ ROWNUM AS ROW_NUM , T851.PD_NM , T850.FGPT_PD_CD , T850.FGPT_QTY , T850.DTL_CNTR_NO , T850.DTL_CNTR_SN FROM TB_SSCT_FGPT_RCP_IZ T850 /* 사은품접수내역 */ INNER JOIN T11 ON T850.DTL_CNTR_NO = T11.CNTR_NO AND T850.DTL_CNTR_SN = T11.CNTR_SN AND T850.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_BAS T851/* T.상품기본 - */ ON T851.PD_CD = T850.FGPT_PD_CD /* 상품코드 */ AND T851.TEMP_SAVE_YN = 'N' /* 임시저장여부 */ AND T851.DTA_DL_YN = 'N' /* 데이터삭제여부 */ ) PIVOT (MAX(DTL_CNTR_NO) AS CNTR_NO , MAX(DTL_CNTR_SN) AS CNTR_SN , MAX(PD_NM) AS PD_NM , MAX(FGPT_PD_CD) AS PD_CD , MAX(FGPT_QTY) AS QTY FOR ROW_NUM IN (1 AS FGPT_1, 2 AS FGPT_2, 3 AS FGPT_3, 4 AS FGPT_4, 5 AS FGPT_5)) ) SELECT /*+ USE_NL(T11 T10 T15 T20 T21 T22 T23 T30 T32 T34 T40 T41 T48 T51 T65 T70 T71 T72 T73 T74 T75 T76 T79 T90 T81 T83 T85 T86) INDEX(T51) NO_MERGE(T71) NO_MERGE(T79) NO_MERGE(T90) NO_MERGE(T81) NO_MERGE(T83) */ T11.CNTR_NO /* 계약번호 */ , T11.CNTR_SN /* 계약번호 일련번호 */ , CONCAT(CONCAT(T11.CNTR_NO, '-'), T11.CNTR_SN) AS CNTR_DTL_NO /* 계약번호 - 계약일련번호 [계약상세번호] */ , T11.SELL_TP_CD /* 판매유형코드 */ , (SELECT F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_DTL_CD', T20.SELL_TP_DTL_CD ) FROM DUAL) AS SELL_TP_DTL_NM /* 판매유형 */ , CASE WHEN T32.OG_LEVL_DV_CD = 2 THEN T32.DGR2_LEVL_DG_PRTNR_NO WHEN T32.OG_LEVL_DV_CD = 1 THEN T32.DGR1_LEVL_DG_PRTNR_NO ELSE T32.DGR3_LEVL_DG_PRTNR_NO END AS DGR3_LEVL_DG_PRTNR_NO /* 지점장사번(LCDBON) - 지점장ID */ , CASE WHEN T32.OG_LEVL_DV_CD = 2 THEN T32.DGR2_LEVL_DG_PRTNR_NM WHEN T32.OG_LEVL_DV_CD = 1 THEN T32.DGR1_LEVL_DG_PRTNR_NM ELSE T32.DGR3_LEVL_DG_PRTNR_NM END AS DGR3_LEVL_DG_PRTNR_NM /* 지점장명(AKDBNM) */ , CASE WHEN T32.OG_LEVL_DV_CD = 2 THEN T32.DGR2_LEVL_OG_CD WHEN T32.OG_LEVL_DV_CD = 1 THEN T32.DGR1_LEVL_OG_CD ELSE T32.DGR3_LEVL_OG_CD END AS DGR3_LEVL_OG_CD /* 지점코드(LCDDPT) 조직코드*/ , T30.OG_CD /* 조직코드(판매자의조직코드) */ , T10.SELL_PRTNR_NO /* 판매자사번 (LCDCDE) */ , T30.PRTNR_KNM /* 판매자명(AKDNAM) */ , T40.CRAL_LOCARA_TNO /* 판매자-휴대지역전화번호 (AKDBTL) */ , T40.MEXNO_EN