SELECT CONCAT(CONCAT(T10.CNTR_NO, '-'), T10.CNTR_SN) AS CNTR_DTL_NO /* 계약번호 - 계약일련번호 [계약상세번호] */ , T10.CNTR_NO /* 계약번호 [계약번호] */ , T10.CNTR_SN /* 계약일련번호 [계약번호] */ , T10.SELL_TP_CD /* 판매유형코드 */ , T12.CST_KNM /* 고객한글명 [계약자명] */ , T12.COPN_DV_CD /* 법인격구분코드(계약자 고객구분 ) 1:개인, 2:법인 */ , F_CMZ_CD_NM ('TNT_WELLS', 'COPN_DV_CD', T12.COPN_DV_CD) AS COPN_DV_NM /* 고객구분명 [고객구분]*/ /* 개인인경우 생년월일, 사업자인경우 사업자 등록번호로 조회 하려 했으나 실명구분코드와 실명번호 암호화로 대체 처리하여도 되는지 데이터 확인 후 진행 */ , T12.BZRNO /* 사업자등록번호(계약자) */ , T12.BRYY_MMDD /* 생년월일(계약자) */ , T12.SEX_DV_CD /* 성별구분코드(계약자) */ , CASE WHEN T12.COPN_DV_CD = '1' /* 계약자 고객구분 1(개인) 인 경우 */ THEN T12.BRYY_MMDD WHEN T12.COPN_DV_CD = '2' /* 계약자 고객구분 2(법인) 인 경우 */ THEN T12.BZRNO ELSE '' END AS RNMNO /* [주민/사업자번호] */ , T12.RNMNO_DV_CD /* 실명번호구분코드 01:주민등록번호, 02:외국인등록번호, 03:법인등록번호, 04:사업자등록번호, 05:단체고유번호 */ , T12.RNMNO_ENCR /* 실명번호암호화 */ , T10.BASE_PD_CD /* 기준상품코드[상품코드] */ /* 기준상품만을 조건으로 조회하려 했으나 복합상품, 기준상품, 서비스, 교재 제품등 해당 상품 유형이 확인 되지 않아 MAX로 조회 */ , ( SELECT MAX(T13.PD_NM) /* 상품기본.상품명 */ FROM TB_PDBS_PD_BAS T13 /* 상품기본 */ WHERE T13.PD_CD = T10.BASE_PD_CD /* 상품기본.상품코드 = 계약상세.기준상품코드 */ AND T13.DTA_DL_YN = 'N' /* 상품기본.데이터삭제여부가 'Y'가 아닌 건 */ ) AS BASE_PD_NM /* [상품명] */ , T10.SELL_DSC_DV_CD /* 계약상세.할인적용유형코드 [할인구분]*/ , F_CMZ_CD_NM( 'TNT_WELLS' , (SELECT CT52.USER_DFN_02 AS ID FROM T_CMZ_CD_D CT52 WHERE 1 = 1 AND CT52.CD_ID = 'SELL_DSC_DV_CD' AND CT52.CD_VLD_VAL = T10.SELL_TP_CD AND CT52.TENANT_ID = 'TNT_WELLS') , T10.SELL_DSC_DV_CD ) AS SELL_DSC_DV_NM , T10.SELL_DSC_TP_CD /* 계약상세.할인적용상세코드 [할인유형] */ /* 할인유형명 계층형으로 되어있음 */ , F_CMZ_CD_NM( 'TNT_WELLS' , (SELECT CM2.USER_DFN_02 FROM T_CMZ_CD_D CM2 WHERE 1 = 1 AND CM2.CD_ID = 'SELL_DSC_TP_CD' --DSC_APY_TP_CD AND CM2.PRTS_CD_VLD_VAL = T10.SELL_TP_CD AND CM2.TENANT_ID = 'TNT_WELLS') , T10.SELL_DSC_TP_CD ) AS SELL_DSC_TP_NM , T11.CNTR_RCP_FSH_DTM /* 계약기본.계약접수완료일시 [접수일] */ , T10.SPP_DUEDT /* 계약상세.배송예정일자 [예정일] */ , T10.CNTR_PD_STRTDT AS SL_DT /* 매출일자는 계약상세.계약상품시작일자로 대체 */ , CASE WHEN TRIM(T11.CNTR_CAN_DTM) IS NOT NULL /* 계약기본.계약취소일시 값이 있으면 */ THEN SUBSTR(TRIM(T11.CNTR_CAN_DTM),1,8) /* 계약상세.계약상품종료일자와 길이 맞춰줌 */ ELSE CASE WHEN T10.CNTR_DTL_STAT_CD = '303' /* 계약상세.계약상세상태코드 = '303'(계약취소) TODO: 고객요청해약(301), 연체해약(303) 포함여부 확인 필요 */ THEN T10.CNTR_PD_ENDDT /* 계약상품종료일자 */ ELSE '' END END AS CNTR_CAN_DT /* 계약취소일 [취소일] , T15.CPS_DT /* 계약WELLS상세.보상일자 [보상일] */ /* 매출경과일 항목 조회 기준 확정 이후 처리 - 해당항목 as-is에 없음 * as-is LC3000.LCDEMY/LCDEMM/LCDEMD 예정년월일 + 14일 * to-be 매핑정보 기준 배송예정일자 + 14일 */ , CASE WHEN TRIM(T10.SPP_DUEDT) IS NOT NULL THEN TO_CHAR(TO_DATE(T10.SPP_DUEDT, 'YYYYMMDD')+14, 'YYYYMMDD') ELSE '' END AS SL_PASG_DT /* [매출경과일] */ , CASE WHEN TRIM(T11.CNTR_CNFM_APR_DTM) IS NOT NULL THEN 'Y' ELSE 'N' END SL_CNFM_YN /* [매출확정여부] */ , T11.CNTR_CAN_RSON_CD /* 계약기본.계약취소사유코드 [취소유형] */ , F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_CAN_RSON_CD', T11.CNTR_CAN_RSON_CD) AS CAN_RSON_NM /* [취소유형명] */ , T40.CNTR_CH_RSON_CD AS CNTR_CH_DTL_RSON_CD /* , T10.CNTR_CH_DTL_RSON_CD 계약상세.계약변경상세사유코드 TODO: 계약변경 상세변경상세사유코드 삭제 여부 확인 */ , F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_CH_RSON_CD', T40.CNTR_CH_RSON_CD) AS CNTR_CH_DTL_RSON_NM /* [계약변경명] TODO: 계약변경 상세변경상세사유코드 삭제 여부 확인 */ , T10.FRISU_YN /* 계약상세.무상여부 [무료체험여부] */ , '' AS FRE_EXPN_CNFM_DTM /* TODO : 무료체험 확정일 매핑정보 확인 필요 무료체험기본.무료체험확정일시(TB_SSOP_FRE_EXPN_BAS.FRE_EXPN_CNFM_DTM)가 있으나 JOIN 정보 확인 불가 */ , T10.CTT_RS_CD AS CTT_RS_CD /* 계약상세.컨택결과코드 [컨택코드] */ , F_CMZ_CD_NM ('TNT_WELLS', 'LC_CTT_RS_CD', T10.CTT_RS_CD) AS CTT_RS_NM /* 컨택코드명 - 공통코드LC컨택결과코드 참조 [컨택코드명] */ -- , (SELECT MAX(T17.SPP_MTHD_TP_CD) KEEP (DENSE_RANK FIRST ORDER BY T18.SPP_DUEDT DESC NULLS LAST, T17.IOST_DTM DESC NULLS LAST) AS IOST_DTL_CD /* 배송주문기본.배송방식유형코드 */ -- FROM TB_SSSO_SPP_ORD_DTL T17 /* 배송주문상세 */ -- INNER JOIN TB_SSSO_SPP_ORD_BAS T18 /* 배송주문기본 */ -- ON T18.SPP_ORD_NO = T17.SPP_ORD_NO /* 배송주문기본.배송주문번호 = 배송주문상세.배송주문번호 */ -- AND T18.DTA_DL_YN = 'N' -- AND T17.DTA_DL_YN = 'N' -- WHERE T17.CNTR_NO = T10.CNTR_NO -- AND T17.CNTR_SN = T10.CNTR_SN -- AND T18.IOST_TP_CD = '10' /* 배송주문기본.입출고유형코드 = 10(출고) */ -- ) AS IOST_DTL_CD /* 매핑은 배송주문기본.배송방식유형코드(TB_SSSO_SPP_ORD_DTL.SPP_MTHD_TP_CD) [출고구분] */ , '' AS IOST_DTL_CD /* WELLS 출고 알수 없음 */ -- , ( -- SELECT MAX(T51.SPP_FSH_DT) -- FROM TB_SSCT_FGPT_RCP_IZ T19 /* 사은품접수내역 */ -- INNER JOIN TB_SSSO_SPP_PLAN_BAS T50 /* 배송계획기본 */ -- ON T50.FGPT_RCP_ID = T19.FGPT_RCP_ID /* 배송계획기본.사은품접수ID = 사은품접수내역.사은품접수ID */ -- AND T50.CNTR_NO = T19.DTL_CNTR_NO -- AND T50.CNTR_SN = T19.DTL_CNTR_SN -- AND T50.DTA_DL_YN = 'N' -- AND T19.DTA_DL_YN = 'N' -- INNER JOIN TB_SSSO_SPP_ORD_DTL T171 /* 배송주문상세 */ -- ON T171.SPP_PLAN_ID = T50.SPP_PLAN_ID -- AND T171.CNTR_NO = T19.DTL_CNTR_NO -- AND T171.CNTR_SN = T19.DTL_CNTR_SN -- AND T171.DTA_DL_YN = 'N' -- INNER JOIN TB_SSOP_SPP_BZS_ULD_HIST T51/* 배송업체업로드이력 */ -- ON T51.CNTR_NO = T171.CNTR_NO -- AND T51.CNTR_SN = T171.CNTR_SN -- AND T51.BF_ORD_NO = T171.SPP_ORD_NO -- AND T51.DTA_DL_YN = 'N' -- WHERE T19.DTL_CNTR_NO = T10.CNTR_NO -- AND T19.DTL_CNTR_SN = T10.CNTR_SN -- ) AS SPP_IVC_CRT_DTM /* 배송업체송장처리내역.배송송장생성일시 [사은품택배송장출력일] */ , '' AS SPP_IVC_CRT_DTM /* 배송업체송장처리내역.배송송장생성일시 [사은품택배송장출력일] */ , CASE WHEN TRIM(T10.BOO_SELL_TP_CD) IS NOT NULL THEN 'Y' ELSE 'N' END BOO_SELL_YN /* 예약판매여부 계약상세.예약판매유형코드가 있으면 Y 그외 N [예약판매여부] */ , T11.CNTR_TP_CD /* 계약기본.계약유형코드 */ , F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_TP_CD', T11.CNTR_TP_CD) AS CNTR_TP_NM /* [판매유형] */ /* AS-IS 업무담당자 최초등록자 */ , (SELECT T2.CST_KNM FROM TB_CUBS_CST_BAS T2 WHERE CST_NO = T11.SELL_PRTNR_NO) AS SELL_PRTNR_NM /* [파트너명] */ , T10.FST_RGST_USR_ID /* 계약상세.최초등록사용자 [업무담당자]*/ , T32.DGR3_LEVL_OG_CD AS FST_RGST_DEPT_ID /* 계약상세.최초등록부서 [조직코드] */ -- , (SELECT MAX(T53.RVE_CD) -- FROM T_CMP_USR_ACO_M T53 /* 사용자기본 */ -- INNER JOIN TB_OGBS_PRTNR_DTL T52 /* 파트너상세 */ -- ON T52.PRTNR_NO = T53.EPNO /* 파트너상세.파트너번호 = 사용자기본.사번 */ -- AND T53.DEL_YN = 'N' -- LEFT OUTER JOIN TB_RVDW_RVE_CD T53 /* 수납코드 */ -- ON T53.RVE_CD = T52.RVE_CD /* 수납코드.수납코드 = 파트너상세.수납코드 */ -- AND T53.DTA_DL_YN = 'N' -- WHERE T53.USR_ID = T10.FST_RGST_USR_ID /* 사용자기본.사용자ID = 계약상세.최초등록사용자 */ -- ) AS RVE_CD /* [수납코드] */ , T31.RVE_CD /* [수납코드] */ , '' AS U