AND T12.CRAL_IDV_TNO = #{cralIdvTno}
AND T11.CNTR_CST_NO LIKE #{cntrCstNo}||'%'
AND T11.CNTR_CAN_DTM IS NULL
AND T10.CNTR_NO = #{cntrNo}
AND T10.CNTR_SN = #{cntrSn}
/* 접수일 */ AND T11.CNTR_RCP_FSH_DTM BETWEEN #{strtDt}||'000000' AND #{endDt}||'235959'
/* 매출일 */ AND T15.IST_DT BETWEEN #{strtDt} AND #{endDt}
/* 취소일 */ AND T11.CNTR_CAN_DTM BETWEEN #{strtDt}||'000000' AND #{endDt}||'235959'
/* 상품분류(대분류) */ AND T21.REF_PD_CLSF_VAL LIKE #{hcsfVal} || '%'
