/* 상품코드 */ AND T10.BASE_PD_CD LIKE '%' || #{pdCd} || '%'
/* 상품명 */ AND T20.PD_NM LIKE '%' || #{pdNm} || '%'
/* 제휴코드 */ AND T65.ALNCMP_CD = #{alncmpCd}
/* 행사코드 */ AND T15.SELL_EV_CD = #{sellEvCd}
/* 기타 */
AND T40.CNTR_CH_RSON_CD IS NOT NULL
AND T10.FRISU_YN = 'Y'
AND TRIM(T10.BOO_SELL_TP_CD) IS NOT NULL
/* 파트너코드 */ AND T11.SELL_PRTNR_NO = #{sellPrtnrNo}
/* 조직코드(총괄단) */ AND T32.DGR1_LEVL_OG_ID IN (
