mapper.update:
UPDATE TB_SSCT_CNTR_BAS
SET CNTR_PRGS_STAT_CD = '50' , CNTR_CNFM_DTM = null
SET CNTR_PRGS_STAT_CD = '60' , CNTR_CNFM_DTM = #{cntrCnfmDtm}
WHERE 1=1 AND CNTR_NO = #{cntrNo}

UPDATE TB_SSCT_CNTR_DTL

    SET CNTR_PD_STRTDT = null


    SET CNTR_PD_STRTDT = #{cntrPdStrtdt}


 WHERE 1=1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.select:
SELECT MIN(BASE_PD_CD) AS BASE_PD_CD , MIN(PD_NM) AS PD_NM , SUM(PD_QTY) AS PD_QTY , SUM(FNL_VAL) AS FNL_VAL , SUM(CTR_VAL) AS CTR_VAL , MIN(OJ_PD_CD) AS OJ_PD_CD , MAX(VER_SN) AS VER_SN , MIN(PD_CLSF_NM) AS PD_CLSF_NM FROM (SELECT /* 정기배송 상품 목록*/ T1.BASE_PD_CD /* 기준상품코드*/ , T2.PD_NM /* 상품명 */ , T1.PD_QTY /* 수량 */ , T3.FNL_VAL /* 단가 LD3050P@LCTAMT */ , T3.CTR_VAL /* 할인 LD3050P@LCRAMT */ , T1.OJ_PD_CD /* 대상상품코드(계약상세의상위상품코드) 패키지코드 LD3050P@LCPKAG */ , T3.VER_SN /* 패키지가격차수 LD3050P@LCPSEQ 맵핑이 없음. 패키지가격차수를 가격산출내역의@버전일련번호로 대체*/ , T4.PD_CLSF_NM /* 상춤분류명 */ FROM TB_SSCT_CNTR_PD_REL T1 /* T.계약상품관계 */ LEFT OUTER JOIN TB_PDBS_PD_BAS T2 /* T.상품기본 - */ ON T2.PD_CD = T1.BASE_PD_CD /* 상품코드 */ AND T2.TEMP_SAVE_YN = 'N' /* 임시저장여부 */ AND T2.DTA_DL_YN = 'N' /* 데이터삭제여부 */ LEFT OUTER JOIN LATERAL (SELECT * FROM (SELECT CNTR_NO , CNTR_SN , CTR_VAL --조정값 , FNL_VAL --최종값 , VER_SN --버전일련번호 , ROW_NUMBER() OVER(ORDER BY VER_SN DESC) AS RN FROM TB_SSCT_CNTR_PRC_CMPT_IZ /* 계약가격산출내역 */ WHERE 1=1 AND CNTR_SN = T1.CNTR_SN AND PD_CD = T1.BASE_PD_CD AND DTA_DL_YN = 'N' ) WHERE RN = 1 ) T3 /* 계약가격산출내역 최신() */ ON T3.CNTR_NO = T1.CNTR_NO AND T3.CNTR_SN = T1.CNTR_SN LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS T4 /* T.상품분류기본 상품분류 */ ON T4.PD_CLSF_ID = T2.PD_CLSF_ID /* 상품분류ID */ AND T4.DTA_DL_YN = 'N' -- LEFT OUTER JOIN TB_SSCT_CNTR_PRC_CMPT_IZ T3 /* 계약가격산출내역 - 할인가 */ -- ON T3.CNTR_NO = T1.CNTR_NO -- AND T3.CNTR_SN = T1.CNTR_SN -- AND T3.DTA_DL_YN = 'N' -- AND T60.PD_PRC_FNL_DTL_ID = 'TODO...' /* 상품가격최종상세ID */ -- AND T60.PRC_ID = 'TODO.가격ID' WHERE 1=1
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T1.PD_REL_TP_CD IN ('01','08') /* 상품관계유형코드 : 복합-기준(01), 패키지(Package):08 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN VL_STRT_DTM AND VL_END_DTM AND T1.VL_END_DTM = '99991231235959' /* 유효종료일시 */ ) GROUP BY BASE_PD_CD

SELECT T0.FGPT_PD_CD /* 사은품 상품코드 */ , T0.FGPT_QTY /* 사은품수량 */ --, T3.SPP_FSH_RGST_DTM /* 배송송장생성일시 */ , (SELECT MAX(T4.PD_NM) /* 상품기본.상품명 */ FROM TB_PDBS_PD_BAS T4 /* 상품기본 */ WHERE T4.PD_CD = T0.FGPT_PD_CD /* 상품기본.상품코드 = 계약상세.기준상품코드 */ AND T4.DTA_DL_YN = 'N' /* 상품기본.데이터삭제여부가 'Y'가 아닌 건 */ ) AS FGPT_PD_NM /* [상품정보] */ FROM TB_SSCT_FGPT_RCP_IZ T0 /* 사은품접수내역 */ INNER JOIN TB_SSSO_SPP_PLAN_BAS T1 /* 배송계획기본 */ ON T1.FGPT_RCP_ID = T0.FGPT_RCP_ID /* 배송계획기본.사은품접수ID = 사은품접수내역.사은품접수ID */ AND T1.DTA_DL_YN = 'N' AND T0.DTA_DL_YN = 'N' AND T1.CNTR_NO = T0.DTL_CNTR_NO AND T1.CNTR_SN = T0.DTL_CNTR_SN INNER JOIN TB_SSSO_SPP_ORD_DTL T2 /* 배송주문상세 */ ON T2.SPP_PLAN_ID = T1.SPP_PLAN_ID AND T2.CNTR_NO = T0.DTL_CNTR_NO AND T2.CNTR_SN = T0.DTL_CNTR_SN AND T2.DTA_DL_YN = 'N' -- INNER JOIN TB_SSSO_SPP_BZS_IVC_PROCS_IZ T3 /* 배송업체송장처리내역 */ -- ON T3.CNTR_NO = T2.CNTR_NO -- AND T3.CNTR_SN = T2.CNTR_SN -- AND T3.BF_ORD_NO = T2.BFCH_SPP_ORD_NO -- AND T3.DTA_DL_YN = 'N' WHERE 1=1
AND T0.DTL_CNTR_NO = #{cntrNo}
AND T0.DTL_CNTR_SN = #{cntrSn}
