INSERT INTO TB_SVPD_CST_SV_RGBSPR_IZ (
      CNTR_NO
    , CNTR_SN
    , SV_BIZ_MCLSF_CD
    , SV_BIZ_DCLSF_CD
    , WK_SN
    , VST_DUEDT
    , SV_BIZ_DCLSF_CD
    , FILT_CHNG_LV_CD
    , IST_NMN_N
    , VST_NMN_N
    , ITM_KND_CD
    , PART_PD_CD
    , PART_USE_QTY
    , MTR_CAN_DT
    , MTR_CAN_RSON_CD
    , VST_DV_CD
    , FST_RGST_DTM
    , FST_RGST_USR_ID
    , FST_RGST_PRG_ID
    , FST_RGST_DEPT_ID
    , FNL_MDFC_DTM
    , FNL_MDFC_USR_ID
    , FNL_MDFC_PRG_ID
    , FNL_MDFC_DEPT_ID
) VALUES (
      #{cntrNo}
    , #{cntrSn}
    , SUBSTR(#{newWrkTypDtl}, 1, 2)
    , #{newWrkTypDtl}
    , (
        SELECT NVL(MAX(TEMP.WK_SN) + 1, 1) AS WK_SN
          FROM TB_SVPD_CST_SV_RGBSPR_IZ TEMP
         WHERE 1=1
           AND TEMP.CNTR_NO = #{cntrNo}
           AND TEMP.CNTR_SN = #{cntrSn}
           AND TEMP.SV_BIZ_MCLSF_CD = SUBSTR(#{newWrkTypDtl}, 1, 2)
           AND TEMP.SV_BIZ_DCLSF_CD = #{newWrkTypDtl}
      )
    , #{chekVstDt}
    , #{newWrkTypDtl}
    , #{chngStg}
    , #{chekInstMths}
    , #{chekCyclMths}          /* CHEK_VST_MTHS -> vstNmnN */
    , #{itemKnd}
    , #{newPartCd}
    , #{qty}
    , #{vVs107CnclDt}
    , (CASE WHEN #{vVs107CnclDt} IS NOT NULL THEN '10' ELSE '' END)
    , #{newVstGb}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{session.userId}
    , #{pgrmId}
    , #{session.ogId}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{session.userId}
    , #{pgrmId}
    , #{session.ogId}
)

mapper.select:
/* NEW_PART_LIST */
/*패키지 변경 요청을 적용한 패키지*/
SELECT NVL(
           (SELECT MAT_NM /* 허남인 이사 확인사항(PART_LIST 를 MAT_NM으로 매핑시킴) */
              FROM TB_SVPD_SDING_AS_AK_IZ T1
             WHERE 1=1
               AND CNTR_NO = #{cntrNo}
               AND CNTR_SN = #{cntrSn}
               AND (
                    SUBSTR(#{chekVstDt},1,6) = SUBSTR(AK_CHDT,1,6)
                    /* cherro ::: 아래쪽 조건이 이상함. 확인이 필요함. */
                   /* OR SUBSTR(#{chekVstDt},1,6) >= TO_CHAR(ADD_MONTHS(TO_DATE(FST_RGST_DTM, 'YYYYMMDD'),1), 'YYYYMM') || '01' */
                )
               AND AK_SN = (SELECT MAX(AK_SN)
                                  FROM TB_SVPD_SDING_AS_AK_IZ
                                 WHERE 1=1
                                   AND CNTR_NO = T1.CNTR_NO
                                   AND CNTR_SN = T1.CNTR_SN)
               AND CNFMDT IS NULL
               AND AS_AK_DV_CD = '1'
               AND MTR_PROCS_STAT_CD != '3'), '') AS NEW_PART_LIST
  FROM DUAL

/* V_VS107_CNCL_DT */
/*배송중지요청체크*/
SELECT NVL(
           (SELECT AK_CHDT
              FROM TB_SVPD_SDING_AS_AK_IZ
             WHERE 1=1
               AND CNTR_NO = #{cntrNo}
               AND CNTR_SN = #{cntrSn}
               AND SUBSTR(AK_CHDT,1,6) = SUBSTR(#{chekVstDt},1,6)
               AND CNFMDT IS NULL
               AND AS_AK_DV_CD = '4'
               AND MTR_PROCS_STAT_CD != '3'), '')
  FROM DUAL

/* 주기표생성 작업 */ SELECT DISTINCT VST_MTHS , WRK_TYP_DTL , LPAD(ROW_NUMBER() OVER(ORDER BY ITEM_KND, PART_CD ASC),2,'0') AS CHNG_STG , ITEM_KND , PART_CD , QTY , VST_GB FROM ( /*최소 공배수 만큼 풀어 놓은 방문 주기표 */ SELECT DISTINCT VST_NMN_N AS VST_MTHS , SV_BIZ_DCLSF_CD AS WRK_TYP_DTL , '' AS CHNG_STG , '5' AS ITEM_KND , A.PART_PD_CD AS PART_CD , A.PART_USE_QTY AS QTY , A.VST_DV_CD AS VST_GB FROM TB_PDBS_RGBS_WK_BASE_DTL A WHERE 1=1 AND A.PDCT_PD_CD = #{newGdsCd} /*22.08.18 */ AND (CASE WHEN #{newGdsCd} = 'WM05106251' THEN '1' WHEN #{newGdsCd} = 'WM05106468' THEN '1' WHEN #{newGdsCd} = 'WM05106469' THEN '1' WHEN #{newGdsCd} = 'WM05106470' THEN '1' WHEN #{newGdsCd} = 'WM05106471' THEN '1' ELSE '2' END) = '1' /*고객선택 패기지를 선택한 경우만 처리하기 위해*/ AND (CASE WHEN LENGTH(#{newPartList}) > 0 THEN '2' ELSE '1' END) = '1' /*자유패키지를 선택하여 변경 한 경우는 KSS 자료가 없는 상태라 파라미터로 받은 값을 처리하기 위해*/
UNION ALL
/*자유선택 패키지 일 경우 입력 받은 파라미터로 처리*/ SELECT DISTINCT A.VST_NMN_N AS VST_MTHS , A.SV_BIZ_DCLSF_CD AS WRK_TYP_DTL , '' AS CHNG_STG , '5' AS ITEM_KND , A.PART_PD_CD AS PART_CD , TO_NUMBER(B.PART_QTY) AS QTY , A.VST_DV_CD AS VST_GB FROM TB_PDBS_RGBS_WK_BASE_DTL A , (SELECT /*행을 구분자에 따라 열로 변경*/ SUBSTR(PART_LIST, 1, INSTR(PART_LIST, ',',1,1)-1) AS PART_CD , SUBSTR(PART_LIST, INSTR(PART_LIST, ',',1,1)+1, (CASE WHEN INSTR(PART_LIST, ',',1,2) > 0 THEN INSTR(PART_LIST, ',',1,2) - INSTR(PART_LIST, ',',1,1)-1 ELSE LENGTH(PART_LIST) END)) AS PART_QTY FROM (/*스트링을 행으로*/ SELECT TRIM(REGEXP_SUBSTR(TXT, '[^|]+', 1, LEVEL)) AS PART_LIST FROM (SELECT #{newPartList} AS TXT FROM DUAL) T1 CONNECT BY INSTR(TXT, '|', 1, LEVEL - 1) > 0)) B WHERE 1=1 AND A.PDCT_PD_CD = #{newGdsCd} AND (CASE WHEN LENGTH(#{newPartList}) > 0 THEN '1' ELSE '2' END) = '1' /*자유패키지를 선택하여 변경 한 경우는 KSS 자료가 없는 상태라 파라미터로 받은 값을 처리하기 위해*/ )
