   SET DTA_DL_YN = 'Y'

 WHERE RCP_SN = #{rcpSn}

mapper.insert:
INSERT INTO TB_SVPD_OUTS_SV_RCP_IZ
     ( RCP_SN
     , SV_CNR_SN
     , ENTRP_DV_CD
     , PD_NM
     , DTA_DL_YN

     )
VALUES
     ( (SELECT COALESCE(MAX(RCP_SN),0) + 1
          FROM TB_SVPD_OUTS_SV_RCP_IZ
       )
     , #{svCnrSn}
     , '7'
     , #{pdNm}
     , 'N'

     )

mapper.update:
