     , SAP_SAVE_LCT_CD   /* SAP저장위치코드 */
     , RTNGD_AK_NO       /* 반품요청번호 */
     , LGST_STR_TP_CD    /* 물류입고유형코드 */
     , STR_AK_RGST_DT    /* 입고요청등록일자 */
     , STR_DUEDT         /* 입고예정일자 */
     , IOST_AK_DV_CD     /* 입출고요청구분코드 */
     , ICHR_PRTNR_NO     /* 담당파트너번호 */
     , TRS_YN            /* 전송여부 */
     , TRS_DTM           /* 전송일시 */
     , DTA_DL_YN         /* 데이터삭제여부 */

     )
VALUES
     (
