mapper.insert:
INSERT INTO TB_IFIN_ITM_OSTR_AK_SEND_ETXT   /* 품목출고요청송신전문 */
     (
       SAP_PLNT_CD           /* SAP플랜트코드 */
     , LGST_OSTR_AK_NO       /* 물류출고요청번호 */
     , SAP_CO_CD             /* SAP회사코드 */
     , SAP_SAVE_LCT_CD       /* SAP저장위치코드 */
     , OSTR_AK_NO            /* 출고요청번호 */
     , OSTR_WARE_NO          /* 출고창고번호 */
     , OSTR_RQDT             /* 출고요청일자 */
     , OSTR_HOP_DT           /* 출고희망일자 */
     , IOST_AK_DV_CD         /* 입출고요청구분코드 */
     , ICHR_PRTNR_NO         /* 담당파트너번호 */
     , ICHR_PRTNR_OG_TP_CD   /* 담당파트너조직유형코드 */
     , TRS_YN                /* 전송여부 */
     , TRS_DTM               /* 전송일시 */
     , DTA_DL_YN             /* 데이터삭제여부 */

     )
VALUES
     (
       #{sapPlntCd}
     , #{lgstOstrAkNo}
     , #{sapCoCd}
     , #{sapSaveLctCd}
     , #{ostrAkNo}
     , #{ostrWareNo}
     , #{ostrRqdt}
     , #{ostrHopDt}
     , #{iostAkDvCd}
     , #{ichrPrtnrNo}
     , #{ichrPrtnrOgTpCd}
     , #{trsYn}
     , #{trsDtm}
     , #{dtaDlYn}

     )

 MERGE INTO TB_IFIN_OSTR_AK_DTL_SEND_ETXT TB_TGT   /* 출고요청상세송신전문 */
 USING
     (
       SELECT #{relNo} AS REL_NO   /* 관계번호 */
            , #{relSn} AS REL_SN   /* 관계일련번호 */
         FROM DUAL
     ) TB_SRC
    ON
     (
           TB_TGT.REL_NO    = TB_SRC.REL_NO
       AND TB_TGT.REL_SN    = TB_SRC.REL_SN
       AND TB_TGT.DTA_DL_YN = 'N'
     )
  WHEN NOT MATCHED THEN
INSERT
     (
       SAP_PLNT_CD            /* SAP플랜트코드 */
     , LGST_OSTR_AK_NO        /* 물류출고요청번호 */
     , OSTR_AK_SN             /* 출고요청일련번호 */
     , MPAC_SN                /* 합포장일련번호 */
     , REL_NO                 /* 관계번호 */
     , REL_SN                 /* 관계일련번호 */
     , SAP_IOST_TP_CD         /* SAP입출고유형코드 */
     , LGST_SPP_MTHD_CD       /* 물류배송방식코드 */
     , LGST_WK_MTHD_CD        /* 물류작업방식코드 */
     , MDLV_PRTNR_NO          /* 배달파트너번호 */
     , ITM_PD_CD              /* 품목상품코드 */
     , OSTR_AK_QTY            /* 출고요청수량 */
     , LGST_ITM_GD_CD         /* 물류품목등급코드 */
     , CST_NO                 /* 고객번호 */
     , CST_NM                 /* 고객명 */
     , CNTR_NO                /* 계약번호 */
     , CNTR_SN                /* 계약일련번호 */
     , SV_CNR_CD              /* 서비스센터코드 */
     , SV_CNR_NM              /* 서비스센터명 */
     , SV_CNR_ICHR_PRTNR_NM   /* 서비스센터담당파트너명 */
     , SV_CNR_LK_TNO_ENCR     /* 서비스센터연계전화번호암호화 */
     , SV_CNR_ADR             /* 서비스센터주소 */
     , OVIV_TP_CD             /* 배차유형코드 */
     , HSMTRL_CLSF_CD         /* 학습지분류코드 */
     , HSMTRL_CLSF_NM         /* 학습지분류명 */
     , BACT_PROCS_QTY         /* 후행처리수량 */
     , RMK_CN                 /* 비고내용 */
     , TRS_YN                 /* 전송여부 */
     , TRS_DTM                /* 전송일시 */
     , DTA_DL_YN              /* 데이터삭제여부 */

     )
VALUES
     (
       #{sapPlntCd}
     , #{lgstOstrAkNo}
     , ( SELECT NVL(MAX(OSTR_AK_SN), 0) + 1
           FROM TB_IFIN_OSTR_AK_DTL_SEND_ETXT
          WHERE SAP_PLNT_CD     = #{sapPlntCd}
            AND LGST_OSTR_AK_NO = #{lgstOstrAkNo} )
     , #{mpacSn}
     , #{relNo}
     , #{relSn}
     , #{sapIostTpCd}
     , #{lgstSppMthdCd}
     , #{lgstWkMthdCd}
     , #{mdlvPrtnrNo}
     , #{itmPdCd}
     , #{ostrAkQty}
     , #{lgstItmGdCd}
     , #{cstNo}
     , #{cstNm}
     , #{cntrNo}
     , #{cntrSn}
     , #{svCnrCd}
     , #{svCnrNm}
     , #{svCnrIchrPrtnrNm}
     , #{svCnrLkTnoEncr}
     , #{svCnrAdr}
     , #{ovivTpCd}
     , #{hsmtrlClsfCd}
     , #{hsmtrlClsfNm}
     , #{bactProcsQty}
     , #{rmkCn}
     , #{trsYn}
     , #{trsDtm}
     , #{dtaDlYn}

     )

 MERGE INTO TB_IFIN_OSTR_AK_PCSV_SEND_ETXT TB_TGT   /* 출고요청택배송신전문 */
 USING
     (
       SELECT #{sapPlntCd} AS SAP_PLNT_CD   /* SAP플랜트코드 */
            , #{ostrAkNo}  AS OSTR_AK_NO    /* 출고요청번호 */
            , #{ostrAkSn}  AS OSTR_AK_SN    /* 출고요청일련번호 */
         FROM DUAL
     ) TB_SRC
    ON
     (
           TB_TGT.SAP_PLNT_CD = TB_SRC.SAP_PLNT_CD
       AND TB_TGT.OSTR_AK_NO  = TB_SRC.OSTR_AK_NO
       AND TB_TGT.OSTR_AK_SN  = TB_SRC.OSTR_AK_SN
       AND TB_TGT.DTA_DL_YN   = 'N'
     )
  WHEN NOT MATCHED THEN
INSERT
     (
       SAP_PLNT_CD             /* SAP플랜트코드 */
     , OSTR_AK_NO              /* 출고요청번호 */
     , OSTR_AK_SN              /* 출고요청일련번호 */
     , LGST_WK_MTHD_CD         /* 물류작업방식코드 */
     , OSTR_NO                 /* 출고번호 */
     , OSTR_SN                 /* 출고일련번호 */
     , OSTR_DT                 /* 출고일자 */
     , SPP_IVC_NO              /* 배송송장번호 */
     , PD_BC_NO                /* 상품바코드번호 */
     , CST_NO                  /* 고객번호 */
     , CST_NM                  /* 고객명 */
     , ADRS_LK_TNO_ENCR        /* 수취인연계전화번호암호화 */
     , ADRS_LK_CRAL_TNO_ENCR   /* 수취인연계휴대전화번호암호화 */
     , ZIP                     /* 우편번호 */
     , BAS_ADR                 /* 기본주소 */
     , DTL_ADR                 /* 상세주소 */
     , LTN_ADR                 /* 지번주소 */
     , PD_CN                   /* 상품내용 */
     , CNTR_NO                 /* 계약번호 */
     , CNTR_SN                 /* 계약일련번호 */
     , CNTR_BC_NO              /* 계약바코드번호 */
     , CNTRT_NM                /* 계약자명 */
     , PRTNR_KNM               /* 파트너한글명 */
     , BOX_QTY                 /* 박스수량 */
     , P_VAL                   /* 포인트값 */
     , RMK_CN                  /* 비고내용 */
     , TRS_YN                  /* 전송여부 */
     , TRS_DTM                 /* 전송일시 */
     , MPAC_SN                 /* 합포장일련번호 */
     , DTA_DL_YN               /* 데이터삭제여부 */

     )
VALUES
     (
       #{sapPlntCd}
     , #{ostrAkNo}
     , #{ostrAkSn}
     , #{lgstWkMthdCd}
     , #{ostrNo}
     , #{ostrSn}
     , #{ostrDt}
     , #{sppIvcNo}
     , #{pdBcNo}
     , #{cstNo}
     , #{cstNm}
     , #{adrLkTnoEncr}
     , #{adrsLkCralTnoEncr}
     , #{zip}
     , #{basAdr}
     , #{dtlAdr}
     , #{ltnAdr}
     , #{pdCn}
     , #{cntrNo}
     , #{cntrSn}
     , #{cntrBcNo}
     , #{cntrtNm}
     , #{prtnrKnm}
     , #{boxQty}
     , #{pVal}
     , #{rmkCn}
     , #{trsYn}
     , #{trsDtm}
     , #{mpacSn}
     , #{dtaDlYn}

     )
