mapper.select:
SELECT WARE_MNGT_PRTNR_NO /*창고관리파트너번호*/
  FROM TB_SVST_MCBY_WARE_IZ
 WHERE APY_YM = #{baseYm}
   AND WARE_NO = #{wareNo}

SELECT COUNT(*) AS CNT
  FROM TB_SVST_MCITM_STOC_IZ
 WHERE ITM_PD_CD = #{itmPdCd}
   AND BASE_YM = #{baseYm}
   AND WARE_NO = #{wareNo}

mapper.update:
UPDATE TB_SVST_MCITM_STOC_IZ
   SET BTD_STOC_A_GD_QTY = #{btdStocAGdQty}
     , PITM_STOC_A_GD_QTY = #{pitmStocAGdQty}

 WHERE BASE_YM =  #{baseYm}
   AND WARE_NO = #{wareNo}
   AND ITM_PD_CD = #{itmPdCd}

UPDATE TB_SVST_MCITM_STOC_IZ
   SET BTD_STOC_E_GD_QTY = #{btdStocEGdQty}
     , PITM_STOC_E_GD_QTY = #{pitmStocEGdQty}

 WHERE BASE_YM =  #{baseYm}
   AND WARE_NO = #{wareNo}
   AND ITM_PD_CD = #{itmPdCd}

UPDATE TB_SVST_MCITM_STOC_IZ
   SET BTD_STOC_R_GD_QTY = #{btdStocRGdQty}
     , PITM_STOC_R_GD_QTY = #{pitmStocRGdQty}

 WHERE BASE_YM =  #{baseYm}
