mapper.select:
SELECT CST_SV_ASN_NO AS CST_SV_ASN_NO
     , ASN_OJ_YM
     , TO_CHAR(ADD_MONTHS(TO_DATE(#{asnOjYm}, 'YYYYMM'), 1), 'YYYYMM') AS CRDOVR_YM
  FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ
 WHERE 1=1
   AND ASN_OJ_YM = #{asnOjYm}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

SELECT CST_SV_ASN_NO AS CST_SV_ASN_NO
     , ASN_OJ_YM AS ASN_OJ_YM
  FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ
 WHERE 1=1
   AND ASN_OJ_YM = #{asnOjYm}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
