mapper.select:
SELECT S1.CST_SV_ASN_NO AS SIDING_CST_SV_ASN_NO
  FROM TB_SVPD_CST_SVAS_IST_ASN_IZ S1
 WHERE 1 = 1
   AND S1.CNTR_NO = #{sidingCntrNo}
   AND S1.CNTR_SN = ${sidingCntrSn}

mapper.insert:
INSERT INTO TB_SVPD_AS_IST_CAN_IZ ( /* AS설치취소내역 */
       SV_BIZ_HCLSF_CD /* 서비스업무대분류코드 */
     , IN_CHNL_DV_CD   /* 입력채널구분코드 */
     , CNTR_NO         /* 계약번호 */
     , CNTR_SN         /* 계약일련번호 */
     , DTA_DL_YN       /* 데이터삭제여부 */
     )
VALUES (
       #{svBizHclsfCd}
     , #{inChnlDvCd}
     , #{cntrNo}
     , ${cntrSn}
     , 'N'
     )

mapper.delete:
UPDATE TB_SVPD_CST_SV_EXCN_IZ /* 고객서비스수행내역 */
   SET DTA_DL_YN = 'Y'

 WHERE 1 = 1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = ${cntrSn}

UPDATE TB_SVPD_CST_SVAS_IST_ASN_IZ /* 고객서비스AS설치배정내역 */
   SET DTA_DL_YN = 'Y'

 WHERE 1 = 1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = ${cntrSn}

UPDATE TB_SVPD_CST_SVAS_IST_OJ_IZ /* 고객서비스AS설치대상내역 */
   SET DTA_DL_YN = 'Y'

 WHERE 1 = 1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = ${cntrSn}
