mapper.select:
SELECT T1.CNTR_NO --계약상세번호 , T1.CNTR_SN , T1.CST_NM --고객명 , T1.RCPDT --접수일 , T1.ACDN_RCP_ID , T1.ACDN_RCP_NM --제목 , T1.ACDN_DTM --사고일시 , T1.AGR_DOC_RCV_YN --합의서수신여부 , T1.PD_NM --제품명 , T1.IST_ADR --설치주소 , T1.IST_DTL_ADR --설치상세주소 , T1.IST_REFER_ADR --설치참고주소 , T1.CPS_PRGS_CD , F_CMZ_CD_NM(#{session.tenantId}, 'CPS_PRGS_CD', T1.CPS_PRGS_CD, #{session.langId}) AS CPS_PRGS_NM , O1.HOO_PRTNR_NO AS CNRLD_NO --등록자사번(센터장사번) , T1.CNRLD_NM -- 등록자명(센터장명) , T1.FSH_DT --완료여부 , T1.VST_DT --방문일자 , T1.LOCARA_TNO --전화번호 , T1.EXNO_ENCR , T1.IDV_TNO , T1.CRAL_LOCARA_TNO --휴대전화번호 , T1.MEXNO_ENCR , T1.CRAL_IDV_TNO , T1.IST_DT --설치일 , T1.FST_RGST_DTM --등록일시 , T1.RCP_MO_CN --접수내용 , T1.ACDN_CAUS_CN --사고원인 , T1.CST_DMD_CN --고객요구 , T1.ACDN_RS_CN --결과 , T1.FNL_MDFC_DTM --수정일자 , T1.SV_CNR_NM --센터명 , T1.IMPTA_RSON_CD , F_CMZ_CD_NM(#{session.tenantId}, 'IMPTA_RSON_CD', T1.IMPTA_RSON_CD, #{session.langId}) AS IMPTA_RSON_NM , T1.TOT_CPS_AMT --총보상액 , T1.KW_CPS_AMT --교원부담 , T1.INSRCO_CPS_AMT --보험사부담 FROM TB_SVPD_ACDN_DOAN_IZ T1 LEFT OUTER JOIN TB_OGBS_MM_OG_IZ O1 ON O1.BASE_YM = SUBSTR(T1.RCPDT,1,6) AND O1.OG_ID = T1.SV_CNR_OG_ID AND O1.OG_LEVL_DV_CD = '1' AND O1.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N'
AND T1.VST_DT BETWEEN #{vstDtFrom} AND #{vstDtTo}
AND T1.SV_CNR_OG_ID = #{svCnrOgId}
AND T1.CST_NM LIKE '%' || #{cstNm} || '%'
AND T1.CNTR_NO = #{cntrNo} AND T1.CNTR_SN = #{cntrsSn}
AND T1.PD_NM LIKE '%' || #{pdNm} || '%'
AND T1.ACDN_RCP_NM LIKE '%' || #{acdnRcpNm} || '%'
AND T1.CPS_PRGS_CD = #{cpsPrgsCd}
AND T1.IMPTA_RSON_CD = #{imptaRsonCd}
AND T1.TOT_CPS_AMT = ${totCpsAmt}
AND T1.FST_RGST_DTM >= #{searchDtFrom}
AND T1.FST_RGST_DTM <= #{searchDtTo}
AND T1.FSH_DT >= #{searchDtFrom}
AND T1.FSH_DT <= #{searchDtTo}

mapper.update:
UPDATE TB_SVPD_ACDN_DOAN_IZ
   SET FSH_DT                  = #{fshDt}
     , CPS_PRGS_CD             = #{cpsPrgsCd}
     , INSRCO_CPS_AMT          = ${insrcoCpsAmt}
     , AGR_DOC_FW_YN           = #{agrDocFwYn}
     , TOT_CPS_AMT             = ${totCpsAmt}
     , KRN_TOT_CPS_AMT_MRK_NM  = #{krnTotCpsAmtMrkNm}
     , KW_CPS_AMT              = ${kwCpsAmt}
     , ACDN_RS_CN              = #{acdnRsCn}
     , CPS_DV_CD               = ${cpsDvCd}

 WHERE ACDN_RCP_ID = #{acdnRcpId}

UPDATE TB_SVPD_ACDN_DOAN_IZ
   SET FML_REL_DV_CD1       = #{fmlRelDvCd1}
     , ETC_CN1              = #{etcCn1}
     , FML_REL_DV_CD2       = #{fmlRelDvCd2}
     , ETC_CN2              = #{etcCn2}
     , MAAS_FNM             = #{maasFnm}
     , MAAS_BRYY_MMDD       = #{maasBirthdate}
     , MAAS_CRAL_LOCARA_TNO = #{cralLocaraTno}
     , MAAS_MEXNO_ENCR      = #{mexnoEncr}
     , MAAS_CRAL_IDV_TNO    = #{cralIdvTno}
     , RFND_ACNO_ENCR       = #{rfndAcnoEncr}
     , RFND_BNK_CD          = #{rfndBnkCd}
     , RFND_ACOWN_NM        = #{rfndAcownNm}
     , AGR_DOC_FW_YN        = #{agrDocFwYn}
     , AGR_DOC_FW_DTM       = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')

 WHERE ACDN_RCP_ID = #{acdnRcpId}

UPDATE TB_SVPD_ACDN_DOAN_IZ
   SET WRTE_DT       = #{wrteDt}
     , CST_SIGN_CN   = #{cstSignCn}

 WHERE ACDN_RCP_ID = #{acdnRcpId}

