mapper.select:
SELECT CNTR_NO --계약상세번호 , CNTR_SN , CST_NM --고객명 , ACDN_RCP_ID , ACDN_RCP_NM --제목 , ACDN_DTM --사고일시 , AGR_DOC_RCV_YN --합의서수신여부 , PD_NM --제품명 , IST_ADR --설치주소 , IST_DTL_ADR --설치상세주소 , IST_REFER_ADR --설치참고주소 , CPS_PRGS_CD --보상진행 TODO:공통코드 등록된 후 수정. , FST_RGST_USR_ID --등록자 , FSH_DT --완료여부 , VST_DT --방문일자 , LOCARA_TNO --전화번호 , EXNO_ENCR , IDV_TNO , CRAL_LOCARA_TNO --휴대전화번호 , MEXNO_ENCR , CRAL_IDV_TNO , SL_DT --매출일 , FST_RGST_DTM --등록일시 , RCP_MO_CN --접수내용 , ACDN_CAUS_CN --사고원인 , CST_DMD_CN --고객요구 , ACDN_RS_CN --결과 , FNL_MDFC_DTM --수정일자 , RPTR_NM --보고자 , SV_CNR_NM --센터명 , BRCH_NM --지점명 , IMPTA_RSON_CD --귀책여부 , TOT_CPS_AMT --총보상액 , KW_CPS_AMT --교원부담 , INSRCO_CPS_AMT --보험사부담 FROM TB_SVPD_ACDN_DOAN_IZ WHERE 1=1 AND DTA_DL_YN = 'N' AND CNTR_NO IS NOT NULL
AND SV_CNR_OG_ID = ${svCnrOgId}
AND CST_NM LIKE '%' || ${cstNm} || '%'
AND CNTR_NO = #{cntrNo} AND CNTR_SN = ${cntrsSn}
AND PD_NM LIKE '%' || ${pdNm} || '%'
AND ACDN_RCP_NM LIKE '%' || ${acdnRcpNm} || '%'
AND CPS_PRGS_CD = ${cpsPrgsCd}
AND IMPTA_RSON_CD = ${imptaRsonCd}
AND TOT_CPS_AMT = ${totCpsAmt}
AND FST_RGST_DTM BETWEEN ${rgstDtmFrom} AND ${rgstDtmTo}
AND FSH_DT BETWEEN ${fshDtFrom} AND ${fshDtTo}

mapper.update:
UPDATE TB_SVPD_ACDN_DOAN_IZ
   SET FSH_DT                  = #{fshDt}
     , CPS_PRGS_CD             = #{cpsPrgsCd}
     , INSRCO_CPS_AMT          = ${insrcoCpsAmt}
     , AGR_DOC_FW_YN           = #{agrDocFwYn}
     , TOT_CPS_AMT             = ${totCpsAmt}
     , KW_CPS_AMT              = ${kwCpsAmt}
     , PSIC_NM                 = #{psicNm}
     , VST_IZ                  = #{vstIz}
     , DAMG_IZ                 = #{damgIz}
     , EST_IZ                  = #{estIz}
     , AGR_IZ                  = #{agrIz}
     , TOT_RDU_AMT             = ${totRduAmt}

 WHERE ACDN_RCP_ID = #{acdnRcpId}
